let real_data = []
let datasets = []
Object.keys(date_data).forEach(date => {
    Object.keys(chart_data).forEach(language => {
        if (real_data[language] == null) {
            real_data[language] = [];
        }
        real_data[language].push(chart_data[language][date])
    });
});

Object.keys(chart_data).forEach(language => {
    datasets.push({
        axis: 'y',
        label: capitalizeFirstLetter(language) + ' Sentiment',
        data: real_data[language],
        fill: false,
        borderWidth: 1
    })

});
